    border: 2px solid #ddd;
    border-radius: 5px;
    font-size: 16px;
    font-family: inherit;
}

#taskDescription {
    min-height: 80px;
    resize: vertical;
}

button {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
