    cursor: pointer;
    font-size: 16px;
    font-weight: bold;
    transition: transform 0.2s;
}

button:hover {
    transform: scale(1.05);
}

button:active {
    transform: scale(0.98);
}

.task {
    background: #fff;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
