}

.task-actions {
    display: flex;
    gap: 10px;
}

.task-actions button {
    padding: 8px 15px;
    font-size: 14px;
}

.complete-btn {
    background: linear-gradient(135deg, #11998e 0%, #38ef7d 100%);
}

.delete-btn {
    background: linear-gradient(135deg, #eb3349 0%, #f45c43 100%);
}

#tasksList:empty::before {
    content: "No tasks yet. Add your first task above!";
    display: block;
    text-align: center;
    color: #999;
    padding: 40px;
    font-style: italic;
}

