    padding: 0;
    transition: background 0.2s, transform 0.2s;
}

.close-btn:hover {
    background: #d0d0d0;
    transform: scale(1.05);
}

.profile-avatar {
    font-size: 80px;
    text-align: center;
    margin-bottom: 20px;
}

.profile-info {
    display: flex;
    flex-direction: column;
    gap: 15px;
}

.profile-field {
    display: flex;
    justify-content: space-between;
    padding: 10px 0;
    border-bottom: 1px solid #e0e0e0;
}

.profile-field:last-child {
    border-bottom: none;
}

.profile-field label {
    font-weight: bold;
    color: #555;
