/* Header Controls */
.header-controls {
    display: flex;
    align-items: center;
    gap: 15px;
}

/* Dark Mode Toggle Switch */
.dark-mode-switch {
    position: relative;
    display: inline-block;
    width: 50px;
    height: 26px;
}

.dark-mode-switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

.switch-slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #ccc;
    transition: 0.4s;
    border-radius: 26px;
}

.switch-slider:before {
    position: absolute;
    content: "";
    height: 20px;
    width: 20px;
    left: 3px;
    bottom: 3px;
    background-color: white;
    transition: 0.4s;
    border-radius: 50%;
}

.dark-mode-switch input:checked + .switch-slider {
    background-color: #667eea;
}

.dark-mode-switch input:checked + .switch-slider:before {
    transform: translateX(24px);
}


